  $(window).load(function () {
    var $container = $('.portfolio-items');
    var $filter = $('.filter');
    // Initialize isotope
    $container.isotope({
      filter: '*',
      layoutMode: 'fitRows',
      animationOptions: {
        duration: 750,
        easing: 'linear'
      }
    });
    // Filter items when filter link is clicked
    $filter.find('a').click(function () {
      var selector = $(this).attr('data-filter');
      $filter.find('a').removeClass('current');
      $(this).addClass('current');
      $container.isotope({
        filter: selector,
        animationOptions: {
          animationDuration: 750,
          easing: 'linear',
          queue: false,
        }
      });
      return false;
    });
  });

  /* Overlay Vertical Alignment
   --------------------------------------------------*/

  $.fn.extend({
    verticalAlign: function () {
      //Iterate over the current set of matched elements
      return this.each(function () {
        var obj = $(this);
        // calculate the new padding and height
        var childHeight = obj.height();
        var parentHeight = obj.parent().height();
        var diff = Math.round(((parentHeight - childHeight) / 2));
        // apply new values
        obj.css({
          "display": "block",
          "margin-top": diff
        });
      });
    }
  });
  var callback = function () {
    $(".project-item .overlay .details").verticalAlign();
    $(".team-thumbnail .overlay .details").verticalAlign();
    $(".news-thumbnail .overlay .details").verticalAlign();
  };
  $(document).ready(callback);
